function setAccess( formObj, accessType ) {
	var formNodes   = formObj.nodes;
	var nodesLength = formNodes.length;

	for( var i = 0; i < nodesLength; i++ ) {
		switch( formNodes.item(i).className ) {
			case "subform":
			case "area":
			case "exclGroup":
				AEM_Util.scoCommon.setAccess( formNodes.item(i), accessType );
				break;

			case "field":
				if( formNodes.item(i)["access"] != accessType && formNodes.item(i)["access"] != "protected" ) {
					formNodes.item(i)["access"] = accessType;
				}
				break;

			case "event":
			case "instanceManager":
			case "draw":
			case "ui":
			case "caption":
			case "value":
			case "margin":
			case "validate":
				break;

			default:
				console.println( formNodes.item(i).className + ":" + formNodes.item(i).somExpression );
		}
	}
}

var mandatoryFields = new Array();
function DisableMandatoryFields( formObj ) {
	//mandatoryFields = new Array();

	var formNodes   = formObj.nodes;
	var nodesLength = formNodes.length;

	for( var i = 0; i < nodesLength; i++ ) {
		switch( formNodes.item(i).className ) {
			case "subform":
			case "area":
			case "exclGroup":
				AEM_Util.scoCommon.DisableMandatoryFields( formNodes.item(i));
				//AEM_Util.scoCommon.ReEnableMandatoryFields ( );
				break;

			case "field":
			//
				//console.println( formNodes.item(i).name + ":" + formNodes.item(i).mandatory );
				if( formNodes.item(i).mandatory == "error" ) {
					// keep all submitter's email fields mandatory
					mandatoryFields.push( formNodes.item(i));
					var fieldName = new String( formNodes.item(i).name ).toLowerCase();
					if( fieldName.indexOf( "submittersemail" ) == -1) {
						formNodes.item(i).mandatory = "disabled";
						//console.println("mandatoryFields :" + mandatoryFields.length );
					}

					//console.println("RemandatoryFields:" + RemandatoryFields.length );
