var signatureCount = new Array();
function GetSignatures (formObj)
{		
		var formNodes   = formObj.nodes;
		//console.println("formNodes = " + formNodes.length);

	    for( var i = 0; i < formNodes.length; i++ ) {
			if (formNodes.item(i).className == "subform" || formNodes.item(i).className == "area" ||formNodes.item(i).className == "exclGroup" ){
				GetSignatures(formNodes.item(i));	
				if (formNodes.item(i).name.indexOf("SignatureBlock")!=-1){
					signatureCount.push(formNodes.item(i));
				}
			}
		}
		return signatureCount;
}


function ReEnableMandatoryFields ( ) {
	var arrayLength = mandatoryFields.length;
	for( var i = 0; i < arrayLength; i++ ) {
	mandatoryFields[i].mandatory = "error";

	}
}

function printDateTime_Email_Comment(EMail,Comment)
{
	var date = new Date();
	var pad = "00";
	var sMonth = (date.getMonth()+1).toString();
	var sDate = date.getDate().toString();
    var dateTimeString = date.getFullYear() + "-" + pad.substring(0, pad.length - sMonth.length) + sMonth + "-" + pad.substring(0, pad.length - sDate.length) + sDate + " " + date.toLocaleTimeString();
    Comment = dateTimeString + "   " + EMail + "\n" + Comment + '\n';
   // app.alert(Comment);
}

